import React, {useState, useEffect} from 'react';
import CardDisplay from './card-display/CardDisplay.js';
import Controls from './controls/Controls.js';
import { createSessionRequest } from "./mongoApi.js";
import { dieOptions } from './consts.js';
import { handleRoll } from './controls/Controls.js';

export default function Planechase() {
  function importAll(r, set) {
    let images = {};
    r.keys().forEach((item, index) => {
      images[item.replace(`./img/planes/${set}`, '')] = r(item);
    });
    return images;
  }

  function shuffleDeck(deck) {
    console.log('shuffleDeck');
    for (var i = deck.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = deck[i];
      deck[i] = deck[j];
      deck[j] = temp; 
    }
  }

  function handleCardClick() {
    console.log('handleClick');
    const discardPile = shownCards;
    const tempShuffledCardList = shuffledCardList;
    discardPile.push(tempShuffledCardList.pop());

    setShownCards(discardPile);
    setActiveCard(shownCards[shownCards.length - 1]);
  }

  function makeDecks() {
    console.log('making decks');
    const mtgImages = importAll(
      require.context('./img/planes/mtg', false, /\.(png|jpe?g|svg)$/),
      'mtg'
    );
    const whoImages = importAll(
      require.context('./img/planes/who', false, /\.(png|jpe?g|svg)$/),
      'who'
    );
    const cardImages = [];
    const tempDeck = cardImages;
    const shownCards = [];

    if (sets.indexOf('mtg') > -1) {
      Object.keys(mtgImages).forEach((key) => {
        cardImages.push(mtgImages[key]);
      });
    }

    if (sets.indexOf('who') > -1) {
      Object.keys(whoImages).forEach((key) => {
        cardImages.push(whoImages[key]);
      });
    }

    shuffleDeck(tempDeck);
    shownCards.push(tempDeck.pop());

    return {
      cardList: cardImages,
      shuffledCardList: tempDeck,
      shownCards: shownCards,
    };
  }

  function handleReset() {
    console.log('handleReset');
    setCount(0);
  }

  function getSession() {
    console.log('getSession');
  }

  useEffect(() => {
    console.log('rendering card');
    const stateVars = makeDecks();
    setCardList(stateVars.cardList);
    setShuffledCardList(stateVars.shuffledCardList);
    setShownCards(stateVars.shownCards);
    setActiveCard(shownCards[shownCards.length - 1]);
    setSession(createSessionRequest());
  }, []);

  const [sets, setSets] = useState(['mtg', 'who']);
  const [cardList, setCardList] = useState([]);
  const [shuffledCardList, setShuffledCardList] = useState([]);
  const [shownCards, setShownCards] = useState([]);
  const [activeCard, setActiveCard] = useState(null);
  const [currentResult, setCurrentResult] = useState(dieOptions[0]);
  const [count, setCount] = useState(0);
  const [session, setSession] = useState(null);

  return (
    <div className='container'>
      <div className='controls'>
        <Controls currentResult={currentResult} count={count} handleRoll={handleRoll(setCurrentResult, count, setCount)} handleReset={handleReset}/>
      </div>
      <div className='display'>
        <CardDisplay activeCard={activeCard} handleClick={handleCardClick}/>
      </div>
    </div>
  );
}
