import React, {Component, useState} from 'react'
import blank from '../img/symbols/WHITESPACE.png'
import './CardDisplay.css'

export default function CardDisplay() {
    function importAll(r, set) {
        let images = {};
        r.keys().forEach((item, index) => {
            images[item.replace(`../img/planes/${set}`, "")] = r(item);
        });
        return images;
    }

    function shuffleDeck(deck) {
        console.log('shuffleDeck');
        for (var i = deck.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = deck[i];
            deck[i] = deck[j];
            deck[j] = temp;
        }
    }

    function handleClick() {
        console.log('handleClick');
        const discardPile = shownCards;
        const shuffledCardList = shuffledCardList;
        discardPile.push(shuffledCardList.pop());

        this.setShownCards(discardPile);
    }

    function makeDecks() {
        console.log('making decks');
        const mtgImages = this.importAll(require.context('../img/planes/mtg', false, /\.(png|jpe?g|svg)$/), 'mtg');
        const whoImages = this.importAll(require.context('../img/planes/who', false, /\.(png|jpe?g|svg)$/), 'who');
        const cardImages = [];
        const tempDeck = cardImages;
        const shownCards = [];

        if (sets['mtg']) {
            Object.keys(mtgImages).forEach(key => {
                cardImages.push(mtgImages[key]);
            });
        }

        if (sets['who']) {
            Object.keys(whoImages).forEach(key => {
                cardImages.push(whoImages[key]);
            });
        }

        this.shuffleDeck(tempDeck);
        shownCards.push(tempDeck.pop());

        return {
            cardList: cardImages,
            shuffledCardList: tempDeck,
            shownCards: shownCards
        };
    }

    console.log('rendering card');
    const stateVars = this.makeDecks();

    const [sets, setSets] = useState(['mtg', 'who']);
    const [cardList, setCardList] = useState(stateVars.cardList);
    const [shuffledCardList, setShuffledCardList] = useState(stateVars.shuffledCardList);
    const [shownCards, setShowncards] = useState(stateVars.shownCards);

    this.handleClick = this.handleClick.bind(this);
    const cardToShow = shownCards[shownCards.length - 1];

    return (
        <div className="Card">
            <img key={Date.now()}
                src={cardToShow}
                className="card-img"
                alt={blank}
                onClick={this.handleClick}
            />
        </div>
    ); 
}
