import React, {useState, useEffect} from 'react';
import PlaneDisplay from '../plane-display/PlaneDisplay.js';
import Controls from '../controls/Controls.js';
import { getSession, setPlaneById, setPlaneByName, getAllPlanes, setRollInfo } from "../tools/mongoApi.js";
import { dieOptions } from '../tools/consts.js';
import SessionCode from '../session-code/SessionCode.js';
import { useParams } from 'react-router-dom';

export default function Planechase() {
  function importAll(r, set) {
    let images = {};
    r.keys().forEach((item, index) => {
      images[item.replace(`../img/planes/${set}`, '')] = r(item);
    });
    return images;
  }

  function shuffleDeck(deck) {
    console.log('shuffleDeck');
    for (var i = deck.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = deck[i];
      deck[i] = deck[j];
      deck[j] = temp; 
    }
  }

  function handlePlaneClick() {
    console.log('handleClick');
    const discardPile = shownPlanes;
    const tempShuffledPlaneList = shuffledPlaneList;
    discardPile.push(tempShuffledPlaneList.pop());
    setShownPlanes(discardPile);
    const newActivePlane = shownPlanes[shownPlanes.length - 1]
    setActivePlane(newActivePlane);
    console.log('activePlane: ' + newActivePlane);
    setPlaneById(sessionCode, newActivePlane.id);
  }

  function makeFullPlaneUrl(planeUrl) {
    return planeUrl.indexOf('who') === 0 ? `./who/${planeUrl}` : `./mtg/${planeUrl}`;
  }

  function makeDeckAndShownPlanes(planeList) {
    console.log('making decks');
    const planeImages = importAll(
      require.context('../img/planes', true, /\.(png|jpe?g|svg)$/),
      'mtg'
    );

    const mappedPlaneList = planeList.map(plane => {
      const urlKey = makeFullPlaneUrl(plane.url);
      plane.url = planeImages[urlKey];
      return plane;
    });

    const shuffledDeck = mappedPlaneList;
    const shownPlanes = [];

    shuffleDeck(shuffledDeck);
    shownPlanes.push(shuffledDeck.pop());
    setShuffledPlaneList(shuffledDeck);
    setShownPlanes(shownPlanes);
    return shownPlanes;
  }

  function handleRoll() {
    console.log('handleRoll');
    const randomIdx = Math.floor(Math.random() * dieOptions.length);
    const newResult = dieOptions[randomIdx];
    const newCost = cost + 1;

    setCurrentResult(newResult);
    setCost(newCost);
    setRollInfo(sessionCode, newCost, newResult.name);
  }

  function handleReset() {
    setCost(0);
    setRollInfo(sessionCode, 0, 'Blank');
  }

  const { sessionCode } = useParams();
  const [sets, setSets] = useState(['mtg', 'who']);
  const [shuffledPlaneList, setShuffledPlaneList] = useState([]);
  const [shownPlanes, setShownPlanes] = useState([]);
  const [activePlane, setActivePlane] = useState(null);
  const [currentResult, setCurrentResult] = useState(dieOptions[0]);
  const [cost, setCost] = useState(0);
  const [session, setSession] = useState(null);

  useEffect(() => {
    getAllPlanes().then(planeList => {
      console.log('planeList: ' + planeList.length);
      const shownPlanes = makeDeckAndShownPlanes(planeList);
      // const activePlane = shownPlanes[shownPlanes.length - 1];

      getSession(sessionCode, activePlane).then(session => {
        setSession(session);
        console.log(session);
        setActivePlane(session.plane);
      });
    });
  }, [sessionCode]);

  return (
    <div className='container'>
      <div className='session-code'>
        <SessionCode sessionCode={session ? session.session : null}/>
      </div>
      <div className='controls'>
        <Controls currentResult={currentResult} cost={cost} handleRollFunction={handleRoll} handleResetFunction={handleReset}/>
      </div>
      <div className='display'>
        <PlaneDisplay activePlane={activePlane} handleClick={handlePlaneClick}/>
      </div>
    </div>
  );
}
