import React, {Component, useState} from 'react'
import Planeswalk from '../img/symbols/PW.png'
import Chaos from '../img/symbols/CHAOS.png'
import Blank from '../img/symbols/BLANK.png'
import Whitespace from '../img/symbols/WHITESPACE.png'
import './Controls.css'

export default function Controls({currentResult, count}) {
  console.log('rendering buttons');
  return (
    <div className="Controls">
      <div className="Buttons">
        <button className="Button Roll" onClick={handleRoll}>Roll</button>
        <button className="Button Reset" onClick={handleReset}>Reset</button>
      </div>
      <div className="Display">
        <img key={Date.now()}
            src={currentResult.image}
            className="result-img"
            alt={Whitespace}
        />
        <p>
          <h4>Result: {currentResult.name}</h4>
        </p>
        <p>
          <h4>Your next roll costs {count} mana</h4>
        </p>
      </div>
    </div>
  );
}
