import { getMappedPlane, makeFullPlaneUrl } from "./utils";

const dataSource = 'dhuber-cluster-0';
const database = 'planechase';
const sessionCollection = 'sessions';

function getHeaders() {
  const headers = new Headers();
  headers.append('Content-Type', 'application/json');
  headers.append('Accept', 'application/json');
  headers.append('Access-Control-Request-Headers', '*');
  headers.append('Access-Control-Allow-Origin', '*');
  headers.append('api-key', 'kqhoHk09XphZcWMeBlGUOgDSXwvQDDkkn5mRXreTwyV6afivwZYiTnit6JSt7JyO');
}

export async function getSession(sessionCode, activePlane, imageMap) {
//   curl --location 'https://us-east-1.aws.data.mongodb-api.com/app/data-napyt/endpoint/data/v1/action/findOne' \
// --header 'Content-Type: application/json' \
// --header 'Access-Control-Request-Headers: *' \
// --header 'api-key: kqhoHk09XphZcWMeBlGUOgDSXwvQDDkkn5mRXreTwyV6afivwZYiTnit6JSt7JyO' \
// --header 'Accept: application/json' \
// --data '{
//       "dataSource": "dhuber-cluster-0",
//       "database": "planechase",
//       "collection": "sessions",
//       "filter": { "_id": "ABCD"  }
//   }'

console.log('setting up mongoDb');

if (sessionCode) {
  console.log('getting session ' + sessionCode);
  return await getExistingSession(sessionCode, imageMap);
} 

console.log('making new session');
return await makeNewSession(activePlane, imageMap);
}

async function makeNewSession(activePlane, planarDeck, imageMap) {
  return await fetch(`/planechaseSessions/makeSession/${activePlane.id}`, planarDeck)
    .then(response => response.json())
    .then(data => {
      console.log(data[0]);
      const plane = getMappedPlane(data[1].fields, imageMap);
      return {
        code: data[0].pk,
        plane: plane,
        rollCost: data[0].fields.rollCost,
        rollResult: data[0].fields.rollResult,
        planarDeck: data[0].fields.planarDeck
      };
    })
    .catch(error => console.log('error making session: ' + error));
}

async function getExistingSession(sessionCode, imageMap) {
  // fetch('https://us-east-1.aws.data.mongodb-api.com/app/data-napyt/endpoint/data/v1/action/findOne', requestOptions)
  //   .then(response => response.json())
  //   .then(data => sessionData = data)
  //   .catch(error => console.error(error));
  // console.log('session: ' + sessionData);

  return await fetch(`/planechaseSessions/getSession/${sessionCode}`)
    .then(response => response.json())
    .then(data => {
      console.log(data[0]);
      const plane = getMappedPlane(data[1].fields, imageMap);
      return {
        code: data[0].pk,
        plane: plane,
        rollCost: data[0].fields.rollCost,
        rollResult: data[0].fields.rollResult
      };
    })
    .catch(error => console.log('error getting sessions: ' + error));
}

export async function getAllPlanes() {
  console.log('getAllPlanes');
  return await fetch(`/planechaseSessions/getWebcamFriendlyPlanes`)
    .then(response => response.json())
    .catch(error => console.log('error getting all planes: ' + error));
}

export async function setPlaneById(sessionCode, planeId) {
  console.log('planeId: ' + planeId);
  return await fetch(`/planechaseSessions/setPlane/${sessionCode}/${planeId}`)
    .then(response => response.json())
    .catch(error => console.log('error setting the plane: ' + error));
}

export async function setRollInfo(sessionCode, rollCost, rollResult) {
  console.log(`Roll cost: ${rollCost}, result: ${rollResult}`);
  return await fetch(`/planechaseSessions/setRollInfo/${sessionCode}/${rollCost}/${rollResult}`)
    .then(response => response.json())
    .catch(error => console.log('error setting the roll info: ' + error));
}
