import React, {useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import PlaneDisplay from '../plane-display/PlaneDisplay.js';
import Controls from '../controls/Controls.js';
import { getSession, updateSession } from "../tools/mongoApi.js";
import { dieOptions, planeImages } from '../tools/consts.js';
import SessionCode from '../session-code/SessionCode.js';
import { useParams } from 'react-router-dom';
import { getDieResultFromName } from '../tools/utils.js';

export default function Planechase() {
  function handlePlaneClick() {
    const newPlanarDeck = planarDeck;
    const newPlane = newPlanarDeck.pop();
    setActivePlane(newPlane);
    setPlanarDeck(newPlanarDeck);
    const updates = {
      plane: newPlane,
      planarDeck: newPlanarDeck
    };
    updateSession(session.code, updates);
  }


  function handleRoll() {
    const randomIdx = Math.floor(Math.random() * dieOptions.length);
    const newResult = dieOptions[randomIdx];
    const newCost = rollCost + 1;

    setRollResult(newResult);
    setRollCost(newCost);
    const updates = {
      rollCost: newCost,
      rollResult: newResult.name
    };
    updateSession(session.code, updates);
  }

  function handleReset() {
    setRollCost(0);
    setRollResult(dieOptions[0]);
    const updates = {
      rollCost: 0,
      rollResult: 'Blank'
    };
    updateSession(session.code, updates);
  }

  function setStateInfo(session) {
    setSession(session);
    setActivePlane(session.plane);
    setRollResult(getDieResultFromName(session.rollResult));
    setRollCost(session.rollCost);
    setPlanarDeck(session.planarDeck);
  }

  const { inputSessionCode } = useParams();
  const { state } = useLocation();
  const [sets, setSets] = useState(['mtg', 'who']);
  const [activePlane, setActivePlane] = useState(null);
  const [planarDeck, setPlanarDeck] = useState([]);
  const [rollResult, setRollResult] = useState(dieOptions[0]);
  const [rollCost, setRollCost] = useState(0);
  const [session, setSession] = useState(null);
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    console.log('using effect');
    getSession(inputSessionCode, planeImages).then(session => {
      setStateInfo(session);
    });
    // if (state) {
    //   setStateInfo(state);
    // } else {
    //   getSession(inputSessionCode, planeImages).then(session => {
    //     setStateInfo(session);
    //   });
    // }

    setTimeout(() => {
      setTimer(timer + 1);
    }, '3000');
  }, [timer]);

  // useEffect(() => {
  //   getAllPlanes().then(planeList => {
  //     console.log('planeList: ' + planeList.length);
  //     const planeObj = makeDeckAndShownPlanes(planeList);
  //     const firstPlane = planeObj.shownPlanes[planeObj.shownPlanes.length - 1];
  //     const sessionCode = session ? session.code : inputSessionCode

      // getSession(sessionCode, firstPlane, planeObj.imageMap).then(session => {
      //   setSession(session);
      //   console.log(session);
      //   setActivePlane(session.plane);
      //   setCost(session.rollCost);
      //   setCurrentResult(getDieResultFromName(session.rollResult));
      //   // setTimeout(() => {
      //   //   setTimer(timer + 1);
      //   // }, '2000');
      // });
  //   });
  // }, [inputSessionCode, timer]);

  return (
    <div className='container'>
      <div className='session-code'>
        <SessionCode sessionCode={session ? session.code : null}/>
      </div>
      <div className='controls'>
        <Controls currentResult={rollResult} cost={rollCost} handleRollFunction={handleRoll} handleResetFunction={handleReset}/>
      </div>
      <div className='display'>
        <PlaneDisplay activePlane={activePlane} handleClick={handlePlaneClick}/>
      </div>
    </div>
  );
}
