import React from 'react'
import Whitespace from '../img/symbols/WHITESPACE.png'
import './Controls.css'
import { dieOptions } from '../consts';

export function handleRoll(setCurrentResult, count, setCount) {
  console.log('handleRoll');
  const randomIdx = Math.floor(Math.random() * dieOptions.length);
  const newResult = dieOptions[randomIdx];
  const newCount = count + 1;

  setCurrentResult(newResult);
  setCount(newCount);
}

export default function Controls({currentResult, count, handleRollFunction, handleResetFunction}) {
  return (
    <div className="Controls">
      <div className="Buttons">
        <button className="Button Roll" onClick={handleRollFunction}>Roll</button>
        <button className="Button Reset" onClick={handleResetFunction}>Reset</button>
      </div>
      <div className="Display">
        <img key={Date.now()}
            src={currentResult.image}
            className="result-img"
            alt={Whitespace}
        />
        <p>
          <h4>Result: {currentResult.name}</h4>
        </p>
        <p>
          <h4>Your next roll costs {count} mana</h4>
        </p>
      </div>
    </div>
  );
}
