import {useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllPlanes, makeNewSession } from "../tools/mongoApi.js";
import { getMappedPlaneList } from '../tools/utils.js';
import { planeImages } from '../tools/consts.js';

export default function NewSession() {

  // Make plane deck
  // Make session
  // route to planechase component with session id

  function shuffleDeck(deck) {
    for (var i = deck.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = deck[i];
      deck[i] = deck[j];
      deck[j] = temp; 
    }

    return deck;
  }

  function makeShuffledPlanarDeck(planeList) {
    // const planeImages = importAll(
    //   require.context('../img/planes', true, /\.(png|jpe?g|svg)$/),
    //   'mtg'
    // );

    const mappedPlaneList = getMappedPlaneList(planeList, planeImages);
    const shuffledDeck = shuffleDeck(mappedPlaneList);
    return shuffledDeck
  }

  useEffect(() => {
    getAllPlanes().then(planeList => {
      console.log('planeList: ' + planeList.length);
      const shuffledPlanarDeck = makeShuffledPlanarDeck(planeList);
      const firstPlane = shuffledPlanarDeck.pop();

      makeNewSession(firstPlane, shuffledPlanarDeck).then(session => {
        setSession(session);
        console.log(session);
      });
    });
  }, []);

  const [session, setSession] = useState(null);
  const navigate = useNavigate();
  if (session) {
    navigate(`/${session.code}`, { state: session });
  }
  // return (
  //   <div>
  //     <div className='container'>
  //       <Link to='/49'>new session</Link>
  //     </div>
  //     <Outlet />
  //   </div>
  // );
}
